mapper.select:
/****** [ aps.planResult.planVersionPeggingSelect ] ******/
SELECT VERSION_ID AS CODE_CD
     , VERSION_ID AS CODE_NM
     , CUT_OFF_FLAG
     , VERSION_TYPE_CD
FROM TB_MST_PLAN_VERSION_SUB WITH(NOLOCK)
WHERE COMPANY_CD = @{GV_COMPANY_CD}
  AND BU_CD = @{GV_BU_CD}
  AND PLAN_ID = @{planId}
  AND PROD_PART = @{prodPart}
ORDER BY SEQ DESC

/****** [ aps.planResult.routingIdPeggingSelect ] ******/
SELECT A2.ROUTING_ID AS CODE_CD
     , A2.ROUTING_ID AS CODE_NM
FROM TB_HIS_APS_PLAN_PEGGING A1 WITH(NOLOCK)
INNER JOIN TB_MST_ITEM_PLANT A2 WITH(NOLOCK)
   ON A1.COMPANY_CD = A2.COMPANY_CD
  AND A1.BU_CD = A2.BU_CD
  AND A1.ITEM_CD = A2.ITEM_CD
  AND A2.PLANT_CD = 'WF01'
WHERE A1.COMPANY_CD = @{GV_COMPANY_CD}
  AND A1.BU_CD = @{GV_BU_CD}
  AND A1.PLAN_ID = @{planId}
  AND A1.PROD_PART = @{prodPart}
  AND A1.VERSION_ID = @{planVersion}
GROUP BY A2.ROUTING_ID
