DECLARE @P_BEFORE_WEEK NVARCHAR(6); DECLARE @P_BEFORE_THU NVARCHAR(8); DECLARE @P_BEFORE_SAT NVARCHAR(8); DECLARE @CONFIRM_YN NVARCHAR(1); DECLARE @P_PROD_PLAN_ID NVARCHAR(8); DECLARE @P_CURRENT_DAY DATE; SELECT @P_CURRENT_DAY = DBO.UFN_GETDATE();
SELECT @P_BEFORE_WEEK = YEARWEEK , @P_BEFORE_THU = CONVERT (NVARCHAR (30), DATEADD (DAY, 4, CAST (YYYYMMDD AS DATE)), 112) , @P_BEFORE_SAT = CONVERT (NVARCHAR (30), DATEADD (DAY, 6, CAST (YYYYMMDD AS DATE)), 112) FROM TB_MST_CALENDAR WITH(NOLOCK) WHERE YYYYMMDD = ( SELECT CONVERT (NVARCHAR, DATEADD (DAY, -7, MIN (DTTM)), 112) FROM TB_MST_CALENDAR WITH(NOLOCK) WHERE YEARPWEEK = @{partADimWeek});
SELECT @P_PROD_PLAN_ID = PLAN_ID FROM TB_MST_PLAN_VERSION WHERE COMPANY_CD = @{GV_COMPANY_CD} AND BU_CD = @{GV_BU_CD} AND PLAN_TYPE_CD = 'MP' AND START_WEEK = ( SELECT YEARWEEK FROM TB_MST_CALENDAR WHERE YYYYMMDD = ( SELECT CONVERT (NVARCHAR, DATEADD (DAY, 7, MIN (DTTM)), 112) FROM TB_MST_CALENDAR WHERE YEARWEEK = ( SELECT START_WEEK FROM TB_MST_PLAN_VERSION WHERE COMPANY_CD = @{GV_COMPANY_CD} AND BU_CD = @{GV_BU_CD} AND PLAN_ID = @{planIdPast} AND PLAN_TYPE_CD = 'DP_W' ) ) );
IF (@{ap1_yn} != 'Y' AND @{ap2_yn} != 'Y') SELECT @CONFIRM_YN = 'Y'; ELSE WITH W_CODE AS ( #set($_parameter.groupCd="DAY_INDEX")
) SELECT @CONFIRM_YN = CASE WHEN CD.ATTB_1_CD = 'N' THEN 'Y' ELSE ( SELECT TOP 1 CASE WHEN PLAN_ID = @{planIdPast} THEN 'N' ELSE 'Y' END FROM TB_MST_PLAN_VERSION WITH(NOLOCK) WHERE COMPANY_CD = @{GV_COMPANY_CD} AND BU_CD = @{GV_BU_CD} AND PLAN_TYPE_CD = 'DP_W' AND RELEASE_FLAG = 'Y' ORDER BY PLAN_ID DESC ) END FROM W_CODE CD INNER JOIN TB_MST_CALENDAR CA WITH(NOLOCK) ON CD.CODE_CD = CA.DAY_NM AND CA.YYYYMMDD = @{currentDay};
WITH W_PROD AS ( SELECT MATERIAL_CD AS PROD_LVL2_CD FROM UV_HIER_ITEM WHERE LEVEL = 'L2' ), W_PWEEK AS ( SELECT YEARWEEK , YEARPWEEK , DENSE_RANK() OVER(ORDER BY YEARWEEK ASC) AS RNUM FROM TB_MST_CALENDAR WITH(NOLOCK) WHERE YYYYMMDD BETWEEN @{planStartDay} AND @{toCal} GROUP BY YEARWEEK, YEARPWEEK ), W_CODE_OPEN AS ( #set($_parameter.groupCd="DMD_FROZEN_PERIOD")
), W_FROZEN AS ( SELECT PD.PROD_LVL2_CD , CASE WHEN ISNUMERIC(CD.ATTB_1_CD) = 1 THEN CD.ATTB_1_CD ELSE 1 END AS FROZEN_WEEK FROM W_PROD PD LEFT OUTER JOIN W_CODE_OPEN CD ON PD.PROD_LVL2_CD = CD.CODE_CD ), W_OPEN_LIST AS ( SELECT A1.PROD_LVL2_CD , A2.YEARWEEK , A2.YEARPWEEK FROM W_FROZEN A1 CROSS JOIN W_PWEEK A2 WHERE A2.RNUM > A1.FROZEN_WEEK ), W_CUST_LVL2_CD_OPEN AS ( SELECT CAST(A1.REP_CUST_GROUP_CD AS VARCHAR) AS CUST_LVL2_CD_OPEN , CAST(A2.ATTB_1_CD AS VARCHAR) AS DMD1_SP , CAST(A2.ATTB_2_CD AS VARCHAR) AS DMD2_SP , CAST(A2.ATTB_3_CD AS VARCHAR) AS DMD3_SP , CONVERT(VARCHAR, CASE WHEN A2.ATTB_1_CD = 'Y' OR A2.ATTB_2_CD = 'Y' OR A2.ATTB_3_CD = 'Y' THEN 'Y' ELSE 'N' END) ADJ_REASON FROM TB_MST_REP_CUST_GROUP A1 WITH(NOLOCK) INNER JOIN TB_MST_CODE A2 WITH(NOLOCK) ON A1.BU_CD = A2.BU_CD AND A2.GROUP_CD = 'CUST_CATE' AND A1.CUST_CATE = A2.CODE_CD WHERE A1.COMPANY_CD = @{GV_COMPANY_CD} AND A1.BU_CD = @{GV_BU_CD} AND A1.USE_FLAG = 'Y' AND A2.USE_FLAG = 'Y' ), W_OPEN_LIST_CONFIRM AS ( SELECT * FROM W_OPEN_LIST B1 CROSS JOIN W_CUST_LVL2_CD_OPEN B2 ), #if($_parameter.authorityFlag == "Y") W_OPEN_CONFIRM AS ( SELECT * FROM ( SELECT PROD_LVL2_CD , CUST_LVL2_CD_OPEN , CASE WHEN DEMAND_TYPE COLLATE DATABASE_DEFAULT = 'ADJ_REASON' THEN 'PW' + YEARPWEEK + '_' + DEMAND_TYPE COLLATE DATABASE_DEFAULT + '_YN' ELSE 'PW' + YEARPWEEK + '_' + 'CFM' + '_' + DEMAND_TYPE COLLATE DATABASE_DEFAULT + '_YN' END YEARPWEEK_DEMAND_TYPE , EDIT_YN FROM ( SELECT PROD_LVL2_CD , CUST_LVL2_CD_OPEN , YEARPWEEK , DMD1_SP , DMD2_SP , DMD3_SP , ADJ_REASON FROM W_OPEN_LIST_CONFIRM ) A UNPIVOT ( EDIT_YN FOR DEMAND_TYPE IN (DMD1_SP, DMD2_SP, DMD3_SP, ADJ_REASON) ) AS UPVT ) A PIVOT ( MAX(EDIT_YN) FOR YEARPWEEK_DEMAND_TYPE IN ( #set($strCnt = "") #foreach($hiddenMap in $_parameter.hiddenList)
#if($hiddenMap.CD.indexOf("PW") != -1 and $hiddenMap.CD.indexOf("PW_REMARK_YN") == -1) #if($strCnt == "1") , #end [$hiddenMap.CD] #set($strCnt = "1") #end #end ) ) PVT ), #end W_CODE AS ( #set($_parameter.groupCd="DP_AUTH_CTRL,SO_TYPE")
), W_SALES_WEEK AS ( SELECT SUM( CASE WHEN A1.YYYYMMDD >= CONVERT(NVARCHAR, DATEADD(D, 1, EOMONTH(DATEADD(M, -2, @P_CURRENT_DAY))), 112) AND A1.YYYYMMDD <= CONVERT(NVARCHAR, EOMONTH(DATEADD(M, -1, @P_CURRENT_DAY)), 112) THEN 1 ELSE 0 END ) AS M1_DAYS , SUM( CASE WHEN A1.YYYYMMDD >= CONVERT(NVARCHAR, DATEADD(D, 1, EOMONTH(DATEADD(M, -4, @P_CURRENT_DAY))), 112) AND A1.YYYYMMDD <= CONVERT(NVARCHAR, EOMONTH(DATEADD(M, -1, @P_CURRENT_DAY)), 112) THEN 1 ELSE 0 END ) AS M3_DAYS , SUM( CASE WHEN A1.YYYYMMDD >= CONVERT(NVARCHAR, DATEADD(D, 1, EOMONTH(DATEADD(M, -13, @P_CURRENT_DAY))), 112) AND A1.YYYYMMDD <= CONVERT(NVARCHAR, EOMONTH(DATEADD(M, -1, @P_CURRENT_DAY)), 112) THEN 1 ELSE 0 END ) AS M12_DAYS FROM TB_MST_CALENDAR A1 WHERE A1.YYYYMMDD >= CONVERT(NVARCHAR, DATEADD(D, 1, EOMONTH(DATEADD(M, -13, @P_CURRENT_DAY))), 112) AND A1.YYYYMMDD <= CONVERT(NVARCHAR, EOMONTH(DATEADD(M, -1, @P_CURRENT_DAY)), 112) ), W_ITEM_CUST_GROUP AS ( SELECT CG.COMPANY_CD , CG.BU_CD , CG.CUST_GROUP_CD , IT.ITEM_CD , ISNULL(IT.PROD_LVL3_CD, ' ') AS PROD_LVL3_CD , ISNULL(IT.PROD_LVL3_NM, ' ') AS PROD_LVL3_NM , ISNULL(CM.CUST_LVL2_CD, ' ') AS CUST_LVL2_CD , ISNULL(CM.CUST_LVL2_NM, ' ') AS CUST_LVL2_NM , ISNULL(CM.CUST_NM, ' ') AS CUST_GROUP_NM , ISNULL(IT.ITEM_NM, ' ') AS ITEM_NM , ISNULL(IT.SPEC, ' ') AS SPEC , ISNULL(IT.DRAW_NO, ' ') AS DRAW_NO , CG.REMARK , CG.CFM_CHG_REASON , CG.SALES_QTY_M1 , CG.SALES_QTY_M3 , CG.SALES_QTY_M12 , ROUND(CG.SALES_QTY_M1 / NULLIF((SELECT M1_DAYS FROM W_SALES_WEEK), 0) * 7, 0) AS SALES_QTY_M1_WEEK , ROUND(CG.SALES_QTY_M3 / NULLIF((SELECT M3_DAYS FROM W_SALES_WEEK), 0) * 7, 0) AS SALES_QTY_M3_WEEK , ROUND(CG.SALES_QTY_M12 / NULLIF((SELECT M12_DAYS FROM W_SALES_WEEK), 0) * 7, 0) AS SALES_QTY_M12_WEEK , IT.PROD_LVL2_CD , CM.CUST_LVL2_CD AS CUST_LVL2_CD_HIDDEN , CASE WHEN @CONFIRM_YN = 'N' THEN CG.CONFIRM_YN_CFM ELSE @CONFIRM_YN END AS CONFIRM_YN , CG.SALES_PRICE_KRW AS SALES_PRICE_KRW_HIDDEN , IP.ROUTING_ID , CG.CPFR_YN , CG.SALES_PLAN_YN , CASE WHEN MC1.CODE_CD IS NULL THEN 'N' ELSE 'Y' END AS CLEANING_YN FROM TB_MAP_ITEM_CUST_GROUP CG WITH(NOLOCK) INNER JOIN TB_MST_ITEM IT WITH(NOLOCK) ON CG.COMPANY_CD = IT.COMPANY_CD AND CG.BU_CD = IT.BU_CD AND CG.ITEM_CD = IT.ITEM_CD AND IT.VALID_FLAG = 'Y' AND IT.USE_FLAG = 'Y' #set($treeAlias = "IT")
LEFT OUTER JOIN ( SELECT COMPANY_CD , BU_CD , ITEM_CD , MAX(ROUTING_ID) AS ROUTING_ID , MAX(PUR_LT) AS PUR_LT , MAX(MFG_LT) AS MFG_LT , SUM(SS_QTY) AS SS_QTY , MAX(PROCUR_TYPE) AS PROCUR_TYPE , MAX(RCPT_SL_CD) AS RCPT_SL_CD FROM TB_MST_ITEM_PLANT WITH(NOLOCK) WHERE PLANT_CD = 'WF01' GROUP BY COMPANY_CD, BU_CD, ITEM_CD ) IP ON CG.COMPANY_CD = IP.COMPANY_CD AND CG.BU_CD = IP.BU_CD AND CG.ITEM_CD = IP.ITEM_CD INNER JOIN TB_MST_CUSTOMER CM WITH(NOLOCK) ON CG.COMPANY_CD = CM.COMPANY_CD AND CG.BU_CD = CM.BU_CD AND CG.CUST_GROUP_CD = CM.CUST_CD AND CM.VALID_FLAG = 'Y' LEFT OUTER JOIN TB_MST_CODE MC1 WITH(NOLOCK) ON MC1.BU_CD = IP.BU_CD AND MC1.GROUP_CD = 'CL_SL_CD' AND MC1.CODE_CD = IP.RCPT_SL_CD AND MC1.USE_FLAG = 'Y' WHERE CG.COMPANY_CD = @{GV_COMPANY_CD} AND CG.BU_CD = @{GV_BU_CD} #if($_parameter.pastFlag != "Y") AND CG.SALES_PLAN_YN = 'Y' #end AND CG.VALID_FLAG = 'Y' #if($_parameter.dataCheck == "SALES_PRICE") AND (CG.SALES_PRICE_KRW IS NULL OR CG.SALES_PRICE_KRW = 0) #end #if($_parameter.customerList and $_parameter.customerList.size() > 0) AND EXISTS ( SELECT 1 FROM TB_MST_CUSTOMER TT WHERE TT.COMPANY_CD = CG.COMPANY_CD AND TT.BU_CD = CG.BU_CD AND TT.CUST_GROUP_CD = CG.CUST_GROUP_CD AND TT.VALID_FLAG = 'Y' #set($treeAlias = "TT")
) #end #if($_parameter.item_cd and $_parameter.item_cd != "") AND CG.ITEM_CD IN ('$_parameter.item_cd.replace(",","','")') #elseif($_parameter.item_nm and $_parameter.item_nm != "") AND (IT.ITEM_CD LIKE '%' + @{item_nm} + '%' OR IT.ITEM_NM LIKE '%' + @{item_nm} + '%' ) #end #if($_parameter.remark and $_parameter.remark != "") AND CG.REMARK LIKE '%' + @{remark} + '%' #end #if($_parameter.drawNo and $_parameter.drawNo != "") AND IT.DRAW_NO LIKE '%' + @{drawNo} + '%' #end #if($_parameter.excelFlag == "Y") AND EXISTS ( SELECT 1 FROM TB_MAP_ITEM_CUST_GROUP_AP1 A1 WITH(NOLOCK) INNER JOIN TB_MAP_AP1_AP2 A2 WITH(NOLOCK) ON A1.COMPANY_CD = A2.COMPANY_CD AND A1.AP1_USER_ID = A2.AP1_USER_ID #if($_parameter.goc_yn != "Y") AND (A2.AP2_USER_ID = @{GV_USER_ID} OR A2.AP1_USER_ID = @{GV_USER_ID}) #end WHERE A1.COMPANY_CD = CG.COMPANY_CD AND A1.BU_CD = CG.BU_CD AND A1.ITEM_CD = CG.ITEM_CD AND A1.CUST_GROUP_CD = CG.CUST_GROUP_CD AND A1.USE_FLAG = 'Y' ) #end ), #if($_parameter.excelFlag != "Y") W_MY_SELECT_ITEM AS ( SELECT CG.COMPANY_CD , CG.BU_CD , CG.CUST_GROUP_CD , CG.ITEM_CD FROM TB_MAP_ITEM_CUST_GROUP CG WITH(NOLOCK) WHERE CG.COMPANY_CD = @{GV_COMPANY_CD} AND CG.BU_CD = @{GV_BU_CD} AND EXISTS ( SELECT 1 FROM TB_MAP_ITEM_CUST_GROUP_AP1 A1 WITH(NOLOCK) INNER JOIN TB_MAP_AP1_AP2 A2 WITH(NOLOCK) ON A1.COMPANY_CD = A2.COMPANY_CD AND A1.AP1_USER_ID = A2.AP1_USER_ID #if($_parameter.goc_yn != "Y") AND (A2.AP2_USER_ID = @{GV_USER_ID} OR A2.AP1_USER_ID = @{GV_USER_ID}) #end WHERE A1.COMPANY_CD = CG.COMPANY_CD AND A1.BU_CD = CG.BU_CD AND A1.ITEM_CD = CG.ITEM_CD AND A1.CUST_GROUP_CD = CG.CUST_GROUP_CD AND A1.USE_FLAG = 'Y' ) ), #end #if($dim_SS_QTY == "Y") W_SS_QTY AS ( SELECT CG.COMPANY_CD , CG.BU_CD , CG.ITEM_CD , SUM(IP.SS_QTY) AS SS_QTY FROM W_ITEM_CUST_GROUP CG INNER JOIN TB_MST_ITEM_PLANT IP WITH(NOLOCK) ON CG.COMPANY_CD = IP.COMPANY_CD AND CG.BU_CD = IP.BU_CD AND CG.ITEM_CD = IP.ITEM_CD AND IP.PLANT_CD = 'WF01' AND IP.VALID_FLAG = 'Y' GROUP BY CG.COMPANY_CD, CG.BU_CD, CG.ITEM_CD ), #end #if($dim_INV_QTY == "Y") W_INV_QTY AS ( SELECT CG.COMPANY_CD , CG.BU_CD , CG.ITEM_CD , SUM(IV.INV_QTY) AS INV_QTY FROM W_ITEM_CUST_GROUP CG INNER JOIN UV_DYN_INVENTORY IV WITH(NOLOCK) ON CG.COMPANY_CD = IV.COMPANY_CD AND CG.BU_CD = IV.BU_CD AND CG.ITEM_CD = IV.ITEM_CD AND IV.PLANT_CD = 'WF01' AND IV.INV_DATE = @{qtyDate} GROUP BY CG.COMPANY_CD, CG.BU_CD, CG.ITEM_CD ), #end #if($dim_WIP_MFG_QTY == "Y") W_WIP_MFG_QTY AS ( SELECT DWR.COMPANY_CD , DWR.BU_CD , DPOH.ITEM_CD , SUM(DWR.REMAIN_QTY) AS WIP_MFG_QTY2 FROM TB_DYN_WIP_ROUTING DWR WITH(NOLOCK) INNER JOIN TB_DYN_PROD_ORDER_HDR DPOH WITH(NOLOCK) ON DPOH.COMPANY_CD = DWR.COMPANY_CD AND DPOH.BU_CD = DWR.BU_CD AND DPOH.PROD_ORDER_NO = DWR.PROD_ORDER_NO WHERE DWR.COMPANY_CD = @{GV_COMPANY_CD} AND DWR.BU_CD = @{GV_BU_CD} AND DWR.WIP_DATE = @{qtyDate} GROUP BY DWR.COMPANY_CD, DWR.BU_CD, DPOH.ITEM_CD ), #end #if($dim_TOTAL_OPEN_SO == "Y") W_OPEN_SO AS ( SELECT A1.COMPANY_CD , A1.BU_CD , A1.ITEM_CD , A1.CUST_GROUP_CD , ISNULL(A1.TOTAL_OPEN_SO, 0) AS TOTAL_OPEN_SO FROM ( SELECT SO.COMPANY_CD , SO.BU_CD , CM.CUST_GROUP_CD , SO.ITEM_CD , SUM(SO.SO_QTY - SO.GI_QTY) AS TOTAL_OPEN_SO FROM TB_MST_CUSTOMER CM WITH(NOLOCK) INNER JOIN TB_DYN_SALES_ORDER SO WITH(NOLOCK) ON CM.COMPANY_CD = SO.COMPANY_CD AND CM.BU_CD = SO.BU_CD AND CM.CUST_CD = SO.CUST_CD AND SO.CLOSE_YN = 'N' AND SO.DEL_FLAG = 'N' AND CM.VALID_FLAG = 'Y' INNER JOIN W_CODE CD ON SO.SO_TYPE = CD.CODE_CD AND CD.GROUP_CD = 'SO_TYPE' AND CD.ATTB_1_CD = 'Y' WHERE CM.COMPANY_CD = @{GV_COMPANY_CD} AND CM.BU_CD = @{GV_BU_CD} GROUP BY SO.COMPANY_CD, SO.BU_CD, SO.ITEM_CD, CM.CUST_GROUP_CD ) A1 ), #end #if($dim_SO_QTY == "Y") W_OPEN_SO2 AS ( SELECT A1.COMPANY_CD , A1.BU_CD , A1.CUST_GROUP_CD , A1.ITEM_CD , ISNULL(A1.SO_QTY, 0)AS SO_QTY FROM ( SELECT SO.COMPANY_CD , SO.BU_CD , CM.CUST_GROUP_CD , SO.ITEM_CD , SUM(SO.SO_QTY - SO.GI_QTY) AS SO_QTY FROM TB_MST_CUSTOMER CM WITH(NOLOCK) INNER JOIN TB_DYN_SALES_ORDER SO WITH(NOLOCK) ON CM.COMPANY_CD = SO.COMPANY_CD AND CM.BU_CD = SO.BU_CD AND CM.CUST_CD = SO.CUST_CD AND SO.CLOSE_YN = 'N' AND SO.DEL_FLAG = 'N' AND CM.VALID_FLAG = 'Y' INNER JOIN W_CODE CD ON SO.SO_TYPE = CD.CODE_CD AND CD.GROUP_CD = 'SO_TYPE' AND CD.ATTB_1_CD = 'Y' WHERE CM.COMPANY_CD = @{GV_COMPANY_CD} AND CM.BU_CD = @{GV_BU_CD} GROUP BY SO.COMPANY_CD, SO.BU_CD, SO.ITEM_CD, CM.CUST_GROUP_CD ) A1 ), #end #if($dim_PROD_QTY == "Y") W_DIM_PROD_PR AS ( SELECT B1.COMPANY_CD , B1.BU_CD , B1.ITEM_CD , CASE WHEN ISNULL(PLAN_QTY, 0) - ISNULL(RESULT_QTY, 0) > 0 THEN ISNULL(PLAN_QTY, 0) - ISNULL(RESULT_QTY, 0) ELSE 0 END DIM_PROD_QTY FROM ( SELECT HAPPD.COMPANY_CD , HAPPD.BU_CD , HAPPD.ITEM_CD , SUM(HAPPD.QTY) AS PLAN_QTY FROM TB_HIS_APS_PROD_PLAN_DAILY HAPPD WITH(NOLOCK) WHERE HAPPD.COMPANY_CD = @{GV_COMPANY_CD} AND HAPPD.BU_CD = @{GV_BU_CD} AND HAPPD.PLAN_ID = @P_BEFORE_THU AND HAPPD.YYYYMMDD BETWEEN @P_BEFORE_THU AND @P_BEFORE_SAT GROUP BY HAPPD.COMPANY_CD, HAPPD.BU_CD, HAPPD.ITEM_CD ) B1 LEFT OUTER JOIN ( SELECT PR.COMPANY_CD , PR.BU_CD , PR.ITEM_CD , SUM(PR.QTY) AS RESULT_QTY FROM UV_PROD_RESULT PR WITH(NOLOCK) WHERE PR.COMPANY_CD = @{GV_COMPANY_CD} AND PR.BU_CD = @{GV_BU_CD} AND PR.PLANT_CD = 'WF01' AND PR.PROD_DATE BETWEEN @P_BEFORE_THU AND @P_BEFORE_SAT GROUP BY PR.COMPANY_CD, PR.BU_CD, PR.ITEM_CD ) B2 ON B1.COMPANY_CD = B2.COMPANY_CD AND B1.BU_CD = B2.BU_CD AND B1.ITEM_CD = B2.ITEM_CD ), #end #if($dim_DIM_PROD_PLAN_QTY == "Y") W_DIM_PROD_PLAN_QTY AS ( SELECT CG.COMPANY_CD , CG.BU_CD , CG.ITEM_CD , CG.CUST_GROUP_CD , SUM(CASE WHEN SP.MEAS_CD = 'PROD_PLAN_QTY' THEN ISNULL(SP.QTY, 0) ELSE 0 END) AS DIM_PROD_PLAN_QTY2 , SUM(CASE WHEN SP.MEAS_CD = 'SALES_REMAIN_QTY' THEN ISNULL(SP.QTY, 0) ELSE 0 END) AS DIM_SALES_REMAIN_QTY , SUM(CASE WHEN SP.MEAS_CD = 'PROD_REMAIN_QTY' THEN ISNULL(SP.QTY, 0) ELSE 0 END) AS DIM_PROD_REMAIN_QTY , SUM(CASE WHEN SP.MEAS_CD = 'PROD_1ST_QTY' THEN ISNULL(SP.QTY, 0) ELSE 0 END) AS DIM_PROD_1ST_QTY , SUM(CASE WHEN SP.MEAS_CD = 'PROD_2ND_QTY' THEN ISNULL(SP.QTY, 0) ELSE 0 END) AS DIM_PROD_2ND_QTY , SUM(CASE WHEN SP.MEAS_CD = 'PROD_3RD_QTY' THEN ISNULL(SP.QTY, 0) ELSE 0 END) AS DIM_PROD_3RD_QTY FROM W_ITEM_CUST_GROUP CG #if($_parameter.pastFlag == "Y") INNER JOIN TB_HIS_SALES_PLAN SP WITH(NOLOCK) #elseif($_parameter.pastFlag == "N") INNER JOIN TB_DYN_SALES_PLAN SP WITH(NOLOCK) #end ON CG.COMPANY_CD = SP.COMPANY_CD AND CG.BU_CD = SP.BU_CD AND CG.ITEM_CD = SP.ITEM_CD AND CG.CUST_GROUP_CD = SP.CUST_GROUP_CD WHERE 1 = 1 #if($_parameter.pastFlag == "Y") AND SP.PLAN_ID = @{planIdPast} #end AND SP.YEARPWEEK = @{partADimWeek} AND SP.MEAS_CD IN ('PROD_PLAN_QTY', 'SALES_REMAIN_QTY', 'PROD_REMAIN_QTY', 'PROD_1ST_QTY', 'PROD_2ND_QTY', 'PROD_3RD_QTY') GROUP BY CG.COMPANY_CD, CG.BU_CD, CG.ITEM_CD, CG.CUST_GROUP_CD ), #end #if($dim_DIM_AVAIL_QTY == "Y") W_DIM_AVAIL_QTY AS ( SELECT CG.COMPANY_CD , CG.BU_CD , CG.ITEM_CD , CG.CUST_GROUP_CD , SUM(CASE WHEN SP.MEAS_CD = 'AVAIL_QTY' THEN ISNULL(SP.QTY, 0) ELSE 0 END) AS DIM_AVAIL_QTY2 FROM W_ITEM_CUST_GROUP CG #if($_parameter.pastFlag == "Y") INNER JOIN TB_HIS_SALES_PLAN SP WITH(NOLOCK) #elseif($_parameter.pastFlag == "N") INNER JOIN TB_DYN_SALES_PLAN SP WITH(NOLOCK) #end ON CG.COMPANY_CD = SP.COMPANY_CD AND CG.BU_CD = SP.BU_CD AND CG.ITEM_CD = SP.ITEM_CD AND CG.CUST_GROUP_CD = SP.CUST_GROUP_CD WHERE 1 = 1 #if($_parameter.pastFlag == "Y") AND SP.PLAN_ID = @{planIdPast} #end AND SP.YEARPWEEK = @{partADimWeek} AND SP.MEAS_CD = 'AVAIL_QTY' GROUP BY CG.COMPANY_CD, CG.BU_CD, CG.ITEM_CD, CG.CUST_GROUP_CD ), #end
#if($dim_DIM_CALC_BOH_QTY == "Y") W_DIM_CALC_BOH_QTY AS ( SELECT CG.COMPANY_CD , CG.BU_CD , CG.ITEM_CD , CG.CUST_GROUP_CD , SUM(CASE WHEN SP.MEAS_CD = 'CALC_BOH_QTY' THEN ISNULL(SP.QTY, 0) ELSE 0 END) AS DIM_CALC_BOH_QTY FROM W_ITEM_CUST_GROUP CG #if($_parameter.pastFlag == "Y") INNER JOIN TB_HIS_SALES_PLAN SP WITH(NOLOCK) #elseif($_parameter.pastFlag == "N") INNER JOIN TB_DYN_SALES_PLAN SP WITH(NOLOCK) #end ON CG.COMPANY_CD = SP.COMPANY_CD AND CG.BU_CD = SP.BU_CD AND CG.ITEM_CD = SP.ITEM_CD AND CG.CUST_GROUP_CD = SP.CUST_GROUP_CD WHERE 1 = 1 #if($_parameter.pastFlag == "Y") AND SP.PLAN_ID = @{planIdPast} #end AND SP.YEARPWEEK = @{partADimWeek} AND SP.MEAS_CD = 'CALC_BOH_QTY' GROUP BY CG.COMPANY_CD, CG.BU_CD, CG.ITEM_CD, CG.CUST_GROUP_CD ), #end #if($dim_DIM_PROD_NO_REFLECTION == "Y") W_DIM_PROD_NO_REFLECTION AS ( SELECT A1.COMPANY_CD , A1.BU_CD , A1.CUST_GROUP_CD , A1.ITEM_CD , CASE WHEN ISNULL(A1.DIM_AP2_SP, 0) - ISNULL(A2.DIM_AVAIL_QTY2, 0) < 0 THEN 0 ELSE ISNULL(A1.DIM_AP2_SP, 0) - ISNULL(A2.DIM_AVAIL_QTY2, 0) END DIM_PROD_NO_REFLECTION FROM ( SELECT CG.COMPANY_CD , CG.BU_CD , CG.ITEM_CD , CG.CUST_GROUP_CD , SUM(SP.QTY) AS DIM_AP2_SP FROM W_ITEM_CUST_GROUP CG INNER JOIN TB_HIS_SALES_PLAN SP WITH(NOLOCK) ON CG.COMPANY_CD = SP.COMPANY_CD AND CG.BU_CD = SP.BU_CD AND CG.ITEM_CD = SP.ITEM_CD AND CG.CUST_GROUP_CD = SP.CUST_GROUP_CD WHERE 1 = 1 AND SP.PLAN_ID = @{planIdPast} AND SP.YEARWEEK = @{partADimWeek} AND SP.MEAS_CD = 'AP2_SP' GROUP BY CG.COMPANY_CD, CG.BU_CD, CG.ITEM_CD, CG.CUST_GROUP_CD
) A1 LEFT OUTER JOIN ( SELECT CG.COMPANY_CD , CG.BU_CD , CG.ITEM_CD , CG.CUST_GROUP_CD , SUM(SP.QTY) AS DIM_AVAIL_QTY2 FROM W_ITEM_CUST_GROUP CG #if($_parameter.pastFlag == "Y") INNER JOIN TB_HIS_SALES_PLAN SP WITH(NOLOCK) #elseif($_parameter.pastFlag == "N") INNER JOIN TB_DYN_SALES_PLAN SP WITH(NOLOCK) #end ON CG.COMPANY_CD = SP.COMPANY_CD AND CG.BU_CD = SP.BU_CD AND CG.ITEM_CD = SP.ITEM_CD AND CG.CUST_GROUP_CD = SP.CUST_GROUP_CD WHERE 1 = 1 #if($_parameter.pastFlag == "Y") AND SP.PLAN_ID = @{planIdPast} #end AND SP.YEARWEEK = @{partADimWeek} AND SP.MEAS_CD = 'AVAIL_QTY' GROUP BY CG.COMPANY_CD, CG.BU_CD, CG.ITEM_CD, CG.CUST_GROUP_CD ) A2 ON A1.COMPANY_CD = A2.COMPANY_CD AND A1.BU_CD = A2.BU_CD AND A1.ITEM_CD = A2.ITEM_CD AND A1.CUST_GROUP_CD = A2.CUST_GROUP_CD ), #end #if($dim_OPEN_SO_SP == "Y") W_OPEN_SO_HIDDEN AS ( SELECT A1.COMPANY_CD , A1.BU_CD , A1.ITEM_CD , A1.CUST_GROUP_CD , ISNULL(A1.SO_QTY, 0) AS SO_QTY FROM ( SELECT SO.COMPANY_CD , SO.BU_CD , CM.CUST_GROUP_CD , SO.ITEM_CD , SUM(SO.SO_QTY - SO.GI_QTY) AS SO_QTY FROM TB_MST_CUSTOMER CM WITH(NOLOCK) INNER JOIN TB_DYN_SALES_ORDER SO WITH(NOLOCK) ON CM.COMPANY_CD = SO.COMPANY_CD AND CM.BU_CD = SO.BU_CD AND CM.CUST_CD = SO.CUST_CD AND SO.CLOSE_YN = 'N' AND SO.DEL_FLAG = 'N' AND CM.VALID_FLAG = 'Y' INNER JOIN W_CODE CD ON SO.SO_TYPE = CD.CODE_CD AND CD.GROUP_CD = 'SO_TYPE' AND CD.ATTB_1_CD = 'Y' WHERE CM.COMPANY_CD = @{GV_COMPANY_CD} AND CM.BU_CD = @{GV_BU_CD} GROUP BY SO.COMPANY_CD, SO.BU_CD, SO.ITEM_CD, CM.CUST_GROUP_CD ) A1 ), #end #if($dim_DIM_PROD_PLAN_QTY_REMAINS == "Y") W_DIM_PROD_PLAN_QTY_REMAINS AS ( SELECT A1.COMPANY_CD , A1.BU_CD , A1.ITEM_CD , SUM(A2.QTY) AS DIM_PROD_PLAN_QTY_REMAINS FROM W_ITEM_CUST_GROUP A1 INNER JOIN ( SELECT COMPANY_CD , BU_CD , ITEM_CD , QTY FROM TB_HIS_APS_PROD_PLAN_DAILY WITH(NOLOCK) WHERE PLAN_ID = ( SELECT TOP 1 PLAN_ID FROM TB_MST_PLAN_VERSION WITH(NOLOCK) WHERE COMPANY_CD = @{GV_COMPANY_CD} AND BU_CD = @{GV_BU_CD} AND PLAN_TYPE_CD = 'FP' #if($_parameter.pastFlag == "Y") AND APS_START_DATE <= @{qtyDate} #end AND RELEASE_FLAG = 'Y' ORDER BY APS_START_DATE DESC ) #if($_parameter.pastFlag == "Y") AND YYYYMMDD BETWEEN @{startRemains} AND @{endRemains} #else AND YYYYMMDD BETWEEN @P_START_DATE AND @P_END_DATE #end AND YYYYMMDD >= @{qtyDate} ) A2 ON A1.COMPANY_CD = A2.COMPANY_CD AND A1.BU_CD = A2.BU_CD AND A1.ITEM_CD = A2.ITEM_CD GROUP BY A1.COMPANY_CD, A1.BU_CD, A1.ITEM_CD ), #end W_DIM_AVAIL_CFM_SP AS ( SELECT CG.COMPANY_CD , CG.BU_CD , CG.ITEM_CD , CG.CUST_GROUP_CD , SUM(CASE WHEN SP.MEAS_CD = 'AVAIL_QTY' THEN ISNULL(SP.QTY, 0) ELSE 0 END) AS DIM_AVAIL_QTY_HIDDEN , SUM(CASE WHEN SP.MEAS_CD = 'CFM_SP' THEN ISNULL(SP.QTY, 0) ELSE 0 END) AS DIM_CFM_SP_HIDDEN FROM W_ITEM_CUST_GROUP CG #if($_parameter.pastFlag == "Y") INNER JOIN TB_HIS_SALES_PLAN SP WITH(NOLOCK) #elseif($_parameter.pastFlag == "N") INNER JOIN TB_DYN_SALES_PLAN SP WITH(NOLOCK) #end ON CG.COMPANY_CD = SP.COMPANY_CD AND CG.BU_CD = SP.BU_CD AND CG.ITEM_CD = SP.ITEM_CD AND CG.CUST_GROUP_CD = SP.CUST_GROUP_CD WHERE 1 = 1 #if($_parameter.pastFlag == "Y") AND SP.PLAN_ID = @{planIdPast} #end AND SP.YEARWEEK = @{dimWeek} AND SP.MEAS_CD IN ('AVAIL_QTY', 'CFM_SP') GROUP BY CG.COMPANY_CD, CG.BU_CD, CG.ITEM_CD, CG.CUST_GROUP_CD ), W_DIM AS ( SELECT CAST(CG.COMPANY_CD AS VARCHAR) AS COMPANY_CD , CAST(CG.BU_CD AS VARCHAR) AS BU_CD , CAST(CG.PROD_LVL2_CD AS VARCHAR) AS PROD_LVL2_CD , CAST(CG.CUST_LVL2_CD_HIDDEN AS VARCHAR) AS CUST_LVL2_CD_HIDDEN , CAST(CG.PROD_LVL3_CD AS VARCHAR) AS PROD_LVL3_CD , CAST(CG.PROD_LVL3_NM AS VARCHAR) AS PROD_LVL3_NM , CAST(CG.CUST_LVL2_CD AS VARCHAR) AS CUST_LVL2_CD , CAST(CG.CUST_LVL2_NM AS VARCHAR) AS CUST_LVL2_NM , CAST(CG.CUST_GROUP_CD AS VARCHAR) AS CUST_GROUP_CD , CAST(CG.CUST_GROUP_NM AS VARCHAR) AS CUST_GROUP_NM , CAST(CG.ITEM_CD AS VARCHAR) AS ITEM_CD , CAST(CG.ITEM_NM AS VARCHAR) AS ITEM_NM , CAST(CG.SPEC AS VARCHAR) AS SPEC , CAST(CG.DRAW_NO AS VARCHAR) AS DRAW_NO , CAST(CG.ROUTING_ID AS VARCHAR) AS ROUTING_ID , CAST(ISNULL(CG.REMARK, '') AS VARCHAR) AS REMARK , CAST(ISNULL(CG.CFM_CHG_REASON, '') AS VARCHAR) AS CFM_CHG_REASON , CAST(ISNULL(CG.CONFIRM_YN, 'N') AS VARCHAR) AS CONFIRM_YN , ISNULL(CG.SALES_PRICE_KRW_HIDDEN, 0) AS SALES_PRICE_KRW_HIDDEN , ISNULL(CAST (CG.SALES_PRICE_KRW_HIDDEN AS NVARCHAR(20)), ' ') AS SALES_PRICE_KRW , CONVERT(VARCHAR, ISNULL(CG.SALES_QTY_M1, 0)) AS SALES_1M , CONVERT(VARCHAR, ISNULL(CG.SALES_QTY_M3, 0)) AS SALES_3M , CONVERT(VARCHAR, ISNULL(CG.SALES_QTY_M12, 0)) AS SALES_12M , CONVERT(VARCHAR, ISNULL(CG.SALES_QTY_M1_WEEK, 0)) AS SALES_1M_WEEK , CONVERT(VARCHAR, ISNULL(CG.SALES_QTY_M3_WEEK, 0)) AS SALES_3M_WEEK , CONVERT(VARCHAR, ISNULL(CG.SALES_QTY_M3_WEEK, 0)) AS SALES_3M_WEEK_HIDDEN , CONVERT(VARCHAR, ISNULL(CG.SALES_QTY_M12_WEEK, 0)) AS SALES_12M_WEEK , 'CFM_SP' AS CATEGORY_CD , AC.DIM_AVAIL_QTY_HIDDEN , AC.DIM_CFM_SP_HIDDEN , CG.CLEANING_YN #if($dim_OPEN_SO_SP == "Y") , CONVERT(VARCHAR, ISNULL(SOH.SO_QTY, 0)) AS SO_QTY_HIDDEN #end #if($dim_DIM_PROD_NO_REFLECTION == "Y") , CONVERT(VARCHAR, ISNULL(PNR.DIM_PROD_NO_REFLECTION, 0)) AS DIM_PROD_NO_REFLECTION #end #if($dim_DIM_PROD_PLAN_QTY_REMAINS == "Y") , CONVERT(VARCHAR, ISNULL(DPPQR.DIM_PROD_PLAN_QTY_REMAINS, 0)) AS DIM_PROD_PLAN_QTY_REMAINS #end #if($dim_DIM_CALC_BOH_QTY == "Y") , CONVERT(VARCHAR, ISNULL(DCB.DIM_CALC_BOH_QTY, 0)) AS DIM_CALC_BOH_QTY #end #if($dim_DIM_AVAIL_QTY == "Y") , CONVERT(VARCHAR, ISNULL(AQ.DIM_AVAIL_QTY2, 0)) AS DIM_AVAIL_QTY2 #end #if($dim_DIM_PROD_PLAN_QTY == "Y") , CONVERT(VARCHAR, ISNULL(PPQ.DIM_PROD_PLAN_QTY2, 0)) AS DIM_PROD_PLAN_QTY2 , CONVERT(VARCHAR, ISNULL(PPQ.DIM_SALES_REMAIN_QTY, 0)) AS DIM_SALES_REMAIN_QTY , CONVERT(VARCHAR, ISNULL(PPQ.DIM_PROD_REMAIN_QTY, 0)) AS DIM_PROD_REMAIN_QTY , CONVERT(VARCHAR, ISNULL(PPQ.DIM_PROD_1ST_QTY, 0)) AS DIM_PROD_1ST_QTY , CONVERT(VARCHAR, ISNULL(PPQ.DIM_PROD_2ND_QTY, 0)) AS DIM_PROD_2ND_QTY , CONVERT(VARCHAR, ISNULL(PPQ.DIM_PROD_3RD_QTY, 0)) AS DIM_PROD_3RD_QTY #end #if($dim_PROD_QTY == "Y") , CONVERT(VARCHAR, CASE WHEN ISNULL(CG.CLEANING_YN, 'N') = 'N' THEN ISNULL(PR.DIM_PROD_QTY, 0) + ISNULL(PPQ.DIM_PROD_1ST_QTY, 0) + ISNULL(PPQ.DIM_PROD_2ND_QTY, 0) ELSE ISNULL(PR.DIM_PROD_QTY, 0) + ISNULL(PPQ.DIM_PROD_1ST_QTY, 0) END) AS DIM_PROD_QTY #end #if($dim_SS_QTY == "Y") , CONVERT(VARCHAR, ISNULL(IP.SS_QTY, 0)) AS SS_QTY #end #if($dim_INV_QTY == "Y") , CONVERT(VARCHAR, ISNULL(IV.INV_QTY, 0)) AS INV_QTY #end #if($dim_WIP_MFG_QTY == "Y") , CONVERT(VARCHAR, ISNULL(WP.WIP_MFG_QTY2, 0)) AS WIP_MFG_QTY2 #end #if($dim_TOTAL_OPEN_SO == "Y") , CONVERT(VARCHAR, ISNULL(SO.TOTAL_OPEN_SO, 0)) AS TOTAL_OPEN_SO #else , 0 AS TOTAL_OPEN_SO #end #if($dim_SO_QTY == "Y") , CONVERT(VARCHAR, ISNULL(SO2.SO_QTY, 0)) AS SO_QTY #end #if($_parameter.dataAddSales == "INV_WIP" or $_parameter.dataAddSales == "OPEN_SO_INV_WIP") , (ISNULL(IV.INV_QTY, 0) + ISNULL(WP.WIP_MFG_QTY2, 0)) AS INV_WIP_QTY #else , 0 AS INV_WIP_QTY #end FROM W_ITEM_CUST_GROUP CG LEFT OUTER JOIN W_DIM_AVAIL_CFM_SP AC ON CG.COMPANY_CD = AC.COMPANY_CD AND CG.BU_CD = AC.BU_CD AND CG.ITEM_CD = AC.ITEM_CD AND CG.CUST_GROUP_CD = AC.CUST_GROUP_CD #if($dim_OPEN_SO_SP == "Y") LEFT OUTER JOIN W_OPEN_SO_HIDDEN SOH ON CG.COMPANY_CD = SOH.COMPANY_CD AND CG.BU_CD = SOH.BU_CD AND CG.ITEM_CD = SOH.ITEM_CD AND CG.CUST_GROUP_CD = SOH.CUST_GROUP_CD #end #if($dim_SS_QTY == "Y") LEFT OUTER JOIN W_SS_QTY IP ON CG.COMPANY_CD = IP.COMPANY_CD AND CG.BU_CD = IP.BU_CD AND CG.ITEM_CD = IP.ITEM_CD #end #if($dim_INV_QTY == "Y") LEFT OUTER JOIN W_INV_QTY IV ON CG.COMPANY_CD = IV.COMPANY_CD AND CG.BU_CD = IV.BU_CD AND CG.ITEM_CD = IV.ITEM_CD #end #if($dim_WIP_MFG_QTY == "Y") LEFT OUTER JOIN W_WIP_MFG_QTY WP ON CG.COMPANY_CD = WP.COMPANY_CD AND CG.BU_CD = WP.BU_CD AND CG.ITEM_CD = WP.ITEM_CD #end #if($dim_TOTAL_OPEN_SO == "Y") LEFT OUTER JOIN W_OPEN_SO SO ON CG.COMPANY_CD = SO.COMPANY_CD AND CG.BU_CD = SO.BU_CD AND CG.ITEM_CD = SO.ITEM_CD AND CG.CUST_GROUP_CD = SO.CUST_GROUP_CD #end #if($dim_SO_QTY == "Y") LEFT OUTER JOIN W_OPEN_SO2 SO2 ON CG.COMPANY_CD = SO2.COMPANY_CD AND CG.BU_CD = SO2.BU_CD AND CG.ITEM_CD = SO2.ITE